$(document).ready(function(){$(".product-type input").each(function(){var t=$(this),e=t.next(),i=e.text();e.remove(),t.iCheck({checkboxClass:"icheckbox_sm-blue",radioClass:"radio_sm-blue",insert:i})}),$(".plus").click(function(){var t=$("#items__quantity"),e=parseInt(t.val());t.val(e+1)}),$(".minus").click(function(){var t=$("#items__quantity"),e=parseInt(t.val());e>1&&t.val(e-1)})});